import React, { useEffect, useReducer } from "react";
import { act } from "react-dom/test-utils";
const emailReducer = (state, action) => {
  if (action.type === "USER_INPUT") {
    return {
      value: action.val,
      isValid: action.val.includes("@"),
    };
  }
  return {
    value: "",
    isValid: false,
  };
};
function NewLogin() {
  const [emailState, dispatchEmail] = useReducer(emailReducer, {
    value: "",
    isValid: false,
  });
  const emailChangedHandler = (e) => {
    dispatchEmail({ type: "USER_INPUT", val: e.target.value });
  };
  const passwordChangedHandler = (e) => {
    console.log(e.target.value);
  };
  const submitHandler = (e) => {
    e.preventDefault();
  };
  return (
    <form onSubmit={submitHandler}>
      <div>
        <label>E-Mail</label>
        <input type="email" onChange={emailChangedHandler}></input>
        {emailState.isValid && <p>Please Enter an Valid Email</p>}
      </div>
      <div>
        <label>Password</label>
        <input type="password" onChange={passwordChangedHandler}></input>
      </div>
      <button type="submit">Login</button>
    </form>
  );
}

export default NewLogin;
